UPDATE p\nSET sni_kod = CASE\n    WHEN s.sni_kod_int >= 1000 AND s.sni_kod_int <= 1499 \n        OR s.sni_kod_int >= 1600 AND s.sni_kod_int <= 2439\n    THEN '1'\n    WHEN s.sni_kod_int >= 7000 AND s.sni_kod_int <= 7499 \n    THEN 'W'\n    ELSE p.sni_kod\nEND\nFROM prospekts p\nJOIN (\n    SELECT sni_kod, CAST(sni_kod AS INT) AS sni_kod_int\n    FROM prospekts\n) s ON p.sni_kod = s.sni_kod