-- Normalized tables\nCREATE TABLE products (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    price DECIMAL(10, 2)\n);\n\nCREATE TABLE orders (\n    id INT PRIMARY KEY,\n    product_id INT,\n    FOREIGN KEY (product_id) REFERENCES products(id)\n);\n\n-- Denormalized table with cached product information\nCREATE TABLE orders_denormalized (\n    id INT PRIMARY KEY,\n    product_id INT,\n    product_name VARCHAR(255),\n    product_price DECIMAL(10, 2)\n);