-- Create the base table\nCREATE TABLE BaseEntity (\n    Id INT PRIMARY KEY,\n    CreatedOn DATETIME,\n    CreatedBy VARCHAR(50)\n);\n\n-- Create a table for each entity that inherits from the base table\nCREATE TABLE Customer (\n    Id INT PRIMARY KEY,\n    Name VARCHAR(100),\n    Email VARCHAR(100),\n    CONSTRAINT FK_Customer_BaseEntity FOREIGN KEY (Id) REFERENCES BaseEntity(Id)\n);\n\nCREATE TABLE Order (\n    Id INT PRIMARY KEY,\n    OrderDate DATETIME,\n    Total DECIMAL(10, 2),\n    CONSTRAINT FK_Order_BaseEntity FOREIGN KEY (Id) REFERENCES BaseEntity(Id)\n);