SELECT \n  c1.customer,\n  c1.food_type,\n  100 * c1.food_type_value / SUM(c2.food_type_value) AS food_type_perc,\n  (SELECT 100 * food_type_value / SUM(food_type_value) \n   FROM mytable \n   WHERE customer = 3 AND food_type = c1.food_type) AS ideal_perc\nFROM \n  mytable c1 \n  INNER JOIN mytable c2 \n  ON c1.customer = c2.customer\nGROUP BY \n  c1.customer, c1.food_type, c1.food_type_value