SELECT \n    CustomerID,\n    Service,\n    [Cash] = ISNULL([Cash], 0),\n    [Check] = ISNULL([Check], 0),\n    [Credit] = ISNULL([Credit], 0),\n    [Remote] = ISNULL([Remote], 0),\n    SubTotal = SUM(SubTotal),\n    Tax = SUM(Tax),\n    NetTotal = SUM(NetTotal)\nFROM \n(\n    SELECT \n        CustomerID,\n        Service,\n        TransType,\n        SubTotal,\n        Tax,\n        NetTotal\n    FROM \n        YourTable\n) AS SourceTable\nPIVOT \n(\n    SUM(SubTotal)\n    FOR TransType IN ([Cash], [Check], [Credit], [Remote])\n) AS PivotTable\nGROUP BY \n    CustomerID,\n    Service