-- Create a table to store blobs\nCREATE TABLE documents (\n    id SERIAL PRIMARY KEY,\n    entity_id INTEGER NOT NULL,\n    file_name VARCHAR(255) NOT NULL,\n    file_type VARCHAR(100) NOT NULL,\n    contents BYTEA NOT NULL\n);\n\n-- Insert a blob into the table\nINSERT INTO documents (entity_id, file_name, file_type, contents)\nVALUES (1, 'example.pdf', 'application/pdf', pg_read_binary_file('path/to/example.pdf'));\n\n-- Retrieve a blob from the table\nSELECT contents FROM documents WHERE id = 1;