-- Create a new SSIS package\nCREATE PACKAGE pkgBlobTransfer\nGO\n\n-- Add an Oracle OLE DB Source\nEXEC sp_add_data_source 'Oracle OLE DB Source'\nGO\n\n-- Add a SQL Server Destination\nEXEC sp_add_data_destination 'SQL Server Destination'\nGO\n\n-- Create a Data Flow Task\nCREATE TASK 'Data Flow Task'\nGO\n\n-- Add a Data Flow Source (Oracle OLE DB Source)\nCREATE SOURCE 'Oracle OLE DB Source'\nGO\n\n-- Add a Data Flow Destination (SQL Server Destination)\nCREATE DESTINATION 'SQL Server Destination'\nGO\n\n-- Map the Oracle BLOB column to the SQL Server varbinary column\nCREATE COLUMN 'BLOBColumn' INT IDENTITY(1,1)\nCREATE COLUMN 'SQLServerColumn' varbinary(max)\nGO\n\n-- Execute the package\nEXEC pkgBlobTransfer\nGO