-- Create a new column with the correct precision\nALTER TABLE your_table ADD new_column NUMBER(22, 2);\n\n-- Populate the new column with the rounded values\nUPDATE your_table SET new_column = ROUND(old_column, 2);\n\n-- Drop the original column\nALTER TABLE your_table DROP COLUMN old_column;\n\n-- Rename the new column to the original column name (if desired)\nALTER TABLE your_table RENAME COLUMN new_column TO old_column;