-- Create a new stored procedure to run the EXE file\nCREATE PROCEDURE RunExeFile\nAS\nBEGIN\n    DECLARE @cmd VARCHAR(255)\n    SET @cmd = 'C:\Path\To\Your\ExeFile.exe'\n    EXEC master.sys.xp_cmdshell @cmd\nEND\n\n-- Create a new trigger to run the stored procedure when the table is non-empty\nCREATE TRIGGER RunExeFileTrigger\nON MyTable\nAFTER INSERT\nAS\nBEGIN\n    IF EXISTS (SELECT 1 FROM MyTable)\n    BEGIN\n        EXEC RunExeFile\n    END\nEND