-- Create a queue and service\nCREATE QUEUE MyQueue;\nCREATE SERVICE MyService ON QUEUE MyQueue ('http://schemas.microsoft.com/SQL/Notifications/PostQueryNotification');\n\n-- Create a route for the service\nCREATE ROUTE MyRoute WITH SERVICE 'MyService', ADDRESS 'LOCAL';\n\n-- Create a notification request\nEXEC sys.sp_cdc_add_job @job_type = N'capture';\n\n-- Create a notification query\nSELECT * FROM MyTable\nWHERE 1=1;  -- This query will be used to detect changes\n\n-- Subscribe to the notification\nEXEC sys.sp_cdc_change_job @job_type = N'capture', @enabled = 1;