DECLARE @xml XML\nSET @xml = (SELECT \n                (SELECT \n                     'http://foosite.com/' + RTRIM(li.imageStore)\n                     + '/ImageStore.dll?id=' + RTRIM(li.imageID)\n                     + '&raw=1&rev=' + RTRIM(li.imageVersion) AS imageUrl\n                 FOR XML PATH ('image'), TYPE)\n            FROM ListingImages li\n            FOR XML PATH ('images'), TYPE)\n\nSELECT CONVERT(VARCHAR(MAX), REPLACE(@xml, '&amp;', '&'))