-- Generate INSERT statements for the desired data\nEXEC sp_generate_inserts @table_name = 'your_table_name', @where_clause = 'your_where_clause'\n\n-- Modify the generated INSERT statements to include SET IDENTITY_INSERT\nDECLARE @sql NVARCHAR(MAX)\nSET @sql = (SELECT * FROM #insert_statements)\nSET @sql = REPLACE(@sql, 'INSERT INTO', 'SET IDENTITY_INSERT your_table_name ON; INSERT INTO')\nSET @sql = REPLACE(@sql, 'VALUES', 'VALUES; SET IDENTITY_INSERT your_table_name OFF;')\n\n-- Execute the modified INSERT statements\nEXEC sp_executesql @sql