-- Before formatting\nselect this.Column1, case when this.Column2 is null then 0 else this.Column2 end\nfrom dbo.SomeTable this\n    inner join dbo.AnotherTable another on this.id = another.id\nwhere\n    this.Price > 100\n\n-- After formatting\nSELECT this.Column1, CASE WHEN this.Column2 IS NULL THEN 0 ELSE this.Column2 END\nFROM dbo.SomeTable this\n    INNER JOIN dbo.AnotherTable another ON this.id = another.id\nWHERE\n    this.Price > 100