BEGIN TRANSACTION;\n\nBEGIN TRY\n    -- Insert new TrackString\n    INSERT INTO [dbo].[TrackStrings] ([String])\n    VALUES ('YourNewStringHere');\n\n    -- Get the newly inserted TrackString ID\n    DECLARE @TrackStringId BIGINT = SCOPE_IDENTITY();\n\n    -- Insert new Track with the reference to TrackString\n    INSERT INTO [dbo].[Tracks] ([TrackStringId], [Time])\n    VALUES (@TrackStringId, GETDATE());\n\n    -- Commit the transaction\n    COMMIT TRANSACTION;\n\n    PRINT 'Transaction committed successfully.';\nEND TRY\nBEGIN CATCH\n    -- Rollback the transaction if there's an error\n    ROLLBACK TRANSACTION;\n\n    PRINT 'Error occurred. Transaction rolled back.';\n    PRINT ERROR_MESSAGE();\nEND CATCH