SELECT \n    c.TABLE_NAME, \n    c.COLUMN_NAME, \n    c.IS_NULLABLE, \n    t.name AS USER_DEFINED_DATA_TYPE\nFROM \n    MY_DB.INFORMATION_SCHEMA.COLUMNS c\nINNER JOIN \n    sys.columns sc ON c.COLUMN_NAME = sc.name\nINNER JOIN \n    sys.types t ON sc.system_type_id = t.system_type_id AND sc.user_type_id = t.user_type_id\nWHERE \n    c.COLUMN_NAME = 'OrderId'\nORDER BY \n    c.TABLE_NAME\nGO