-- Create a table type\nCREATE TYPE IdList AS TABLE (id INT PRIMARY KEY);\n\n-- Create a stored procedure that takes the table-valued parameter\nCREATE PROCEDURE GetRows\n    @ids IdList READONLY\nAS\nBEGIN\n    SELECT * \n    FROM table \n    JOIN @ids v ON table.Col = v.id;\nEND;\n\n-- Declare a variable of the table type and insert values\nDECLARE @idList IdList;\nINSERT INTO @idList (id) VALUES (123), (123), (222), ...;\n\n-- Call the stored procedure\nEXEC GetRows @idList;