SELECT *\nFROM (\n  SELECT *, ROW_NUMBER() OVER (ORDER BY id) AS row_num\n  FROM photos\n) AS subquery\nWHERE row_num BETWEEN (SELECT row_num FROM (\n  SELECT *, ROW_NUMBER() OVER (ORDER BY id) AS row_num\n  FROM photos\n) AS subquery WHERE id = currentphoto) - 4\n                  AND (SELECT row_num FROM (\n  SELECT *, ROW_NUMBER() OVER (ORDER BY id) AS row_num\n  FROM photos\n) AS subquery WHERE id = currentphoto) + 4\nORDER BY row_num;