-- Drop the index on upperBaseColumn\nDROP INDEX idxUpperBaseColumn ON testTable;\n\n-- Alter the baseColumn\nALTER TABLE testTable\nALTER COLUMN baseColumn VARCHAR(20);\n\n-- Alter the computed column upperBaseColumn\nALTER TABLE testTable\nALTER COLUMN upperBaseColumn AS (UPPER(baseColumn));\n\n-- Recreate the index on upperBaseColumn\nCREATE INDEX idxUpperBaseColumn ON testTable (upperBaseColumn);