-- Create a collection type\nCREATE TYPE id_list IS TABLE OF NUMBER;\n\n-- Create a procedure that takes the collection as a parameter\nCREATE PROCEDURE get_rows\n    p_ids IN id_list\nAS\nBEGIN\n    FORALL i IN p_ids.FIRST..p_ids.LAST\n    SELECT * \n    FROM table \n    WHERE Col = p_ids(i);\nEND;\n\n-- Declare a variable of the collection type and populate it\nDECLARE\n    id_list id_list := id_list(123, 123, 222, ...);\nBEGIN\n    get_rows(id_list);\nEND;