CREATE VIEW TreasuryYieldCurve AS\nSELECT Year, Value\nFROM TreasuryYieldCurveTable\n\nUNION ALL\n\nSELECT 4, 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 3) + 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 5)\n\nUNION ALL\n\nSELECT 6, 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 5) + 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 7)\n\nUNION ALL\n\nSELECT 8, 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 7) + 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 10)\n\nUNION ALL\n\nSELECT 9, 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 8) + 0.5 * (SELECT Value FROM TreasuryYieldCurveTable WHERE Year = 10)\n\nUNION ALL\n\nSELECT Year, Value\nFROM (\n  SELECT Year, 0.5 * LAG(Value) OVER (ORDER BY Year) + 0.5 * LEAD(Value) OVER (ORDER BY Year) AS Value\n  FROM TreasuryYieldCurveTable\n  WHERE Year BETWEEN 11 AND 19\n) AS Subquery\n\nUNION ALL\n\nSELECT Year, Value\nFROM (\n  SELECT Year, 0.5 * LAG(Value) OVER (ORDER BY Year) + 0.5 * LEAD(Value) OVER (ORDER BY Year) AS Value\n  FROM TreasuryYieldCurveTable\n  WHERE Year BETWEEN 21 AND 29\n) AS Subquery