SELECT\n  c.id,\n  c.title,\n  (SELECT COUNT(*) FROM category_link_section WHERE category_id = c.id AND section_id = 1) AS doc1,\n  (SELECT COUNT(*) FROM category_link_section WHERE category_id = c.id AND section_id = 2) AS doc2,\n  (SELECT COUNT(*) FROM category_link_section WHERE category_id = c.id AND section_id = 3) AS doc3,\n  (SELECT COUNT(*) FROM category_link_section WHERE category_id = c.id AND section_id = 4) AS doc4\nFROM\n  category c