-- Create a log table\nCREATE TABLE ErroneousEntries (\n    Id INT IDENTITY(1,1),\n    ColumnName NVARCHAR(50),\n    ErroneousValue NVARCHAR(50)\n);\n\n-- Import data and log erroneous entries\nINSERT INTO TargetTable (Column1, Column2)\nSELECT \n    CASE \n        WHEN TRY_CAST(SourceColumn1 AS INT) IS NULL \n        THEN -1 \n        ELSE TRY_CAST(SourceColumn1 AS INT) \n    END,\n    CASE \n        WHEN TRY_CAST(SourceColumn2 AS INT) IS NULL \n        THEN -1 \n        ELSE TRY_CAST(SourceColumn2 AS INT) \n    END\nFROM SourceTable;\n\nINSERT INTO ErroneousEntries (ColumnName, ErroneousValue)\nSELECT \n    'Column1',\n    SourceColumn1\nFROM SourceTable\nWHERE TRY_CAST(SourceColumn1 AS INT) IS NULL;\n\nINSERT INTO ErroneousEntries (ColumnName, ErroneousValue)\nSELECT \n    'Column2',\n    SourceColumn2\nFROM SourceTable\nWHERE TRY_CAST(SourceColumn2 AS INT) IS NULL;