INSERT INTO TargetTable (Column1, Column2)\nOUTPUT \n    'Column1',\n    SourceColumn1\n    INTO ErroneousEntries (ColumnName, ErroneousValue)\nSELECT \n    CASE \n        WHEN TRY_CAST(SourceColumn1 AS INT) IS NULL \n        THEN -1 \n        ELSE TRY_CAST(SourceColumn1 AS INT) \n    END,\n    CASE \n        WHEN TRY_CAST(SourceColumn2 AS INT) IS NULL \n        THEN -1 \n        ELSE TRY_CAST(SourceColumn2 AS INT) \n    END\nFROM SourceTable\nWHERE TRY_CAST(SourceColumn1 AS INT) IS NULL OR TRY_CAST(SourceColumn2 AS INT) IS NULL;