CREATE PROCEDURE dbo.spGetData\n    @code AS CHAR(4)\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX);\n\n    IF (@code = 'aaaa')\n        SET @sql = N'Select date From [ServerA].Stock.dbo.Syspara';\n    ELSE IF (@code = 'bbbb')\n        SET @sql = N'Select date From [ServerB].Stock.dbo.Syspara';\n    ELSE IF (@code = 'cccc')\n        SET @sql = N'Select date From [ServerC].Stock.dbo.Syspara';\n    ELSE IF (@code = 'dddd')\n        SET @sql = N'Select date From [ServerD].Stock.dbo.Syspara';\n\n    IF @sql IS NOT NULL\n        EXEC sp_executesql @sql;\nEND\nGO