CREATE VIEW dbo.PostHelpfulness WITH SCHEMABINDING AS\nSELECT \n    PostId,\n    COUNT_BIG(CASE WHEN IsHelpful = 1 THEN 1 END) AS IsHelpfulCount,\n    COUNT_BIG(CASE WHEN IsHelpful = 0 THEN 1 END) AS IsNotHelpfulCount\nFROM \n    dbo.HelpfulPosts\nGROUP BY \n    PostId\nGO\n\nCREATE UNIQUE CLUSTERED INDEX IX_PostHelpfulness ON dbo.PostHelpfulness (PostId)\nGO\n\nCREATE NONCLUSTERED INDEX IX_PostHelpfulness_PostId ON dbo.PostHelpfulness (PostId)\nGO