-- Create a connection to the remote server\nEXEC sp_addlinkedserver 'RemoteServer', 'SQL Server', 'RemoteServerName'\n\n-- Perform CRUD operations using OPENQUERY\nSELECT * FROM OPENQUERY(RemoteServer, 'SELECT * FROM RemoteDatabase.dbo.RemoteTable')\n\nINSERT INTO OPENQUERY(RemoteServer, 'SELECT * FROM RemoteDatabase.dbo.RemoteTable')\nVALUES ('Value1', 'Value2')\n\nUPDATE OPENQUERY(RemoteServer, 'SELECT * FROM RemoteDatabase.dbo.RemoteTable')\nSET Column1 = 'NewValue'\nWHERE Column2 = 'Condition'\n\nDELETE FROM OPENQUERY(RemoteServer, 'SELECT * FROM RemoteDatabase.dbo.RemoteTable')\nWHERE Column2 = 'Condition'