-- Create a linked server\nEXEC sp_addlinkedserver 'RemoteServer', 'SQL Server', 'RemoteServerName'\n\n-- Create a login mapping for the linked server\nEXEC sp_addlinkedsrvlogin 'RemoteServer', 'LocalLogin', 'RemoteLogin', 'RemotePassword'\n\n-- Perform CRUD operations using the linked server\nSELECT * FROM RemoteServer.RemoteDatabase.dbo.RemoteTable\n\nINSERT INTO RemoteServer.RemoteDatabase.dbo.RemoteTable (Column1, Column2)\nVALUES ('Value1', 'Value2')\n\nUPDATE RemoteServer.RemoteDatabase.dbo.RemoteTable\nSET Column1 = 'NewValue'\nWHERE Column2 = 'Condition'\n\nDELETE FROM RemoteServer.RemoteDatabase.dbo.RemoteTable\nWHERE Column2 = 'Condition'