SELECT \n    CASE \n        WHEN value >= 10 AND value < 20 THEN '10-20'\n        WHEN value >= 20 AND value < 32.5 THEN '20-32.5'\n        -- Add more ranges as needed\n        ELSE 'Outside ranges'\n    END AS value_range,\n    COUNT(*) AS count,\n    (COUNT(*) * 1.0 / (SELECT COUNT(*) FROM your_table)) * 100 AS percentage\nFROM your_table\nGROUP BY \n    CASE \n        WHEN value >= 10 AND value < 20 THEN '10-20'\n        WHEN value >= 20 AND value < 32.5 THEN '20-32.5'\n        -- Add more ranges as needed\n        ELSE 'Outside ranges'\n    END\nORDER BY value_range;