SELECT \n  c.field1, \n  c.field2, \n  sp1.first_name AS sp1_first_name, \n  sp1.last_name AS sp1_last_name, \n  sp2.first_name AS sp2_first_name, \n  sp2.last_name AS sp2_last_name, \n  sobc.first_name AS sobc_first_name, \n  sobc.last_name AS sobc_last_name, \n  csc1.first_name AS csc1_first_name, \n  csc1.last_name AS csc1_last_name, \n  csc2.first_name AS csc2_first_name, \n  csc2.last_name AS csc2_last_name, \n  cbc.first_name AS cbc_first_name, \n  cbc.last_name AS cbc_last_name\nFROM \n  contracts c\n  JOIN users sp1 ON c.service_provider_1 = sp1.username\n  JOIN users sp2 ON c.service_provider_2 = sp2.username\n  JOIN users sobc ON c.service_org_business_contact = sobc.username\n  JOIN users csc1 ON c.client_service_contact_1 = csc1.username\n  JOIN users csc2 ON c.client_service_contact_2 = csc2.username\n  JOIN users cbc ON c.client_business_contact = cbc.username\nWHERE \n  c.id = ?