USE msdb\nGO\n\nEXEC sp_update_jobstep\n    @job_id = N'YourJobID',\n    @step_id = 1,\n    @notification_type = N'Windows Event Log',\n    @notification_message = N'Step 1 completed successfully',\n    @event_type = N'Information',\n    @event_id = 100\n\nEXEC sp_update_jobstep\n    @job_id = N'YourJobID',\n    @step_id = 1,\n    @notification_type = N'Windows Event Log',\n    @notification_message = N'Step 1 failed',\n    @event_type = N'Error',\n    @event_id = 101\n\n-- Repeat for each step