CREATE TEMPORARY TABLE lessonstatus_temp AS\nSELECT lesson_id\nFROM lessonstatus\nWHERE user_id = 1;\n\nUPDATE lessonstatus\nINNER JOIN user ON lessonstatus.user_id = user.user_id\nSET user_id = (SELECT user_id FROM user WHERE username = 'too_many_accounts')\nWHERE last_name = 'stupid'\nAND first_name = 'user'\nAND username != 'too_many_accounts'\nAND lessonstatus.lesson_id NOT IN (SELECT lesson_id FROM lessonstatus_temp);