DECLARE @asdf INT;\n\nDECLARE cur CURSOR FOR\nSELECT * FROM xyz;\n\nOPEN cur;\n\nFETCH NEXT FROM cur INTO @asdf, @vcxvc, @dffgdfg;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    INSERT INTO abc (column1, column2, column3)\n    VALUES (@asdf, @vcxvc, @dffgdfg);\n\n    DECLARE @newId INT = SCOPE_IDENTITY();\n\n    UPDATE xyz\n    SET column10 = @newId\n    WHERE ID = @asdf;\n\n    FETCH NEXT FROM cur INTO @asdf, @vcxvc, @dffgdfg;\nEND\n\nCLOSE cur;\nDEALLOCATE cur;