DECLARE @procName sysname;\nDECLARE @sql nvarchar(max);\nDECLARE cur CURSOR FOR SELECT name FROM sys.procedures WHERE type = 'P';\n\nOPEN cur;\nFETCH NEXT FROM cur INTO @procName;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql = 'EXEC ' + @procName;\n    SET COMMAND TIMEOUT 60;\n    BEGIN TRY\n        EXEC sp_executesql @sql;\n        PRINT 'Procedure ' + @procName + ' completed in less than 60 seconds';\n    END TRY\n    BEGIN CATCH\n        IF ERROR_NUMBER() = 1222\n            PRINT 'Procedure ' + @procName + ' timed out after 60 seconds';\n        ELSE\n            THROW;\n    END CATCH;\n    FETCH NEXT FROM cur INTO @procName;\nEND;\n\nCLOSE cur;\nDEALLOCATE cur;