-- Create a temp table to store the results\nCREATE TABLE #Results (\n    COL1 data_type,\n    COL2 data_type,\n    ...\n    COLN data_type\n);\n\n-- Use a set-based operation to populate the temp table\nINSERT INTO #Results (COL1, COL2, ..., COLN)\nSELECT \n    t1.COL1,\n    t2.COL2 / 1.1,  -- float op\n    t3.COL3 + ' ' + t1.COL1,\n    DATEPART(t1.COL1),\n    ...\nFROM \n    TABLE_1 t1\nINNER JOIN \n    TABLE_2 t2 ON t1.key = t2.foreignKey\nINNER JOIN \n    TABLE_3 t3 ON t2.key = t3.foreignKey;\n\n-- Insert the results into your target table\nINSERT INTO MyTable (COL1, COL2, ..., COLN)\nSELECT \n    COL1,\n    COL2,\n    ...\n    COLN\nFROM \n    #Results;\n\n-- Drop the temp table\nDROP TABLE #Results;