CREATE PROC Read_Product (\n    @Filter_ProductID uniqueidentifier,\n    @Filter_IDProductType uniqueidentifier,\n    @Filter_ProductName uniqueidentifier,\n    ...\n) AS BEGIN\n    SELECT p.ProductID, p.IDProductType, p.ProductName, ...\n    FROM tblProduct p\n    LEFT JOIN tblFilter f ON p.ProductID = f.FilterValue AND f.ColumnName = 'ProductID' AND f.FilterType = 1\n    LEFT JOIN tblFilter f2 ON p.IDProductType = f2.FilterValue AND f2.ColumnName = 'IDProductType' AND f2.FilterType = 1\n    ...\n    WHERE (@Filter_ProductID IS NULL OR f.FilterValue IS NOT NULL)\n      AND (@Filter_IDProductType IS NULL OR f2.FilterValue IS NOT NULL)\n      ...\nEND