INSERT INTO MainTable_Option ([MainTableID], [OptionID])\nSELECT \n    ID,\n    CASE \n        WHEN OptionVal1 = 'y' THEN (SELECT OptionID FROM Option WHERE OptionDesc = 'OptionVal1')\n        WHEN OptionVal2 = 'y' THEN (SELECT OptionID FROM Option WHERE OptionDesc = 'OptionVal2')\n        WHEN OptionVal3 = 'y' THEN (SELECT OptionID FROM Option WHERE OptionDesc = 'OptionVal3')\n    END\nFROM DodgyOldTable