CREATE TYPE FilterTableType AS TABLE (\n    ColumnName nvarchar(50),\n    FilterType tinyint,\n    FilterValue nvarchar(4000)\n);\n\nCREATE PROC Read_Product (\n    @Filters FilterTableType READONLY\n) AS BEGIN\n    SELECT p.ProductID, p.IDProductType, p.ProductName, ...\n    FROM tblProduct p\n    INNER JOIN @Filters f ON p.ProductID = f.FilterValue AND f.ColumnName = 'ProductID' AND f.FilterType = 1\n    ...\nEND