DECLARE\n  v_cnt NUMBER;\n  v_sql DBMS_SQL.VARCHAR2A;\nBEGIN\n  SELECT COUNT(*) INTO v_cnt FROM all_tables WHERE table_name = 'TABLE1' AND owner = 'AMS';\n  \n  IF v_cnt = 0 THEN\n    v_sql := DBMS_SQL.VARCHAR2A('CREATE TABLE TABLE1(VALUE VARCHAR2(50) NOT NULL)', \n                                'ALTER TABLE TABLE1 ADD (MYVAL2 NVARCHAR2(10))');\n    DBMS_SQL.EXECUTE(v_sql);\n  END IF;\nEND;