-- Query 1: Joining tables directly\nEXPLAIN SELECT result\nFROM result_tbl\nJOIN test_tbl USING (test_id)\nJOIN sample_tbl USING (sample_id)\nJOIN (SELECT request_id FROM request_tbl WHERE request_status='A') USING (request_id);\n\n-- Query 2: Joining subqueries\nEXPLAIN SELECT result\nFROM (SELECT result, test_id FROM result_tbl) AS sub1\nJOIN (SELECT test_id, sample_id FROM test_tbl) AS sub2 USING (test_id)\nJOIN (SELECT sample_id FROM sample_tbl) AS sub3 USING (sample_id)\nJOIN (SELECT request_id FROM request_tbl WHERE request_status='A') AS sub4 USING (request_id);