CREATE OR REPLACE PACKAGE my_package AS\n  FUNCTION add(a IN NUMBER, b IN NUMBER) RETURN NUMBER;\nEND my_package;\n\nCREATE OR REPLACE PACKAGE BODY my_package AS\n  FUNCTION add(a IN NUMBER, b IN NUMBER) RETURN NUMBER IS\n  BEGIN\n    RETURN a + b;\n  END add;\nEND my_package;\n\nCREATE OR REPLACE PACKAGE test_my_package AS\n  PROCEDURE test_add;\nEND test_my_package;\n\nCREATE OR REPLACE PACKAGE BODY test_my_package AS\n  PROCEDURE test_add IS\n  BEGIN\n    ut.assert.eq(my_package.add(2, 3), 5);\n  END test_add;\nEND test_my_package;