-- Create a table to hold the database names\nCREATE TABLE #Databases (Name sysname);\n\n-- Insert the database names you want to run the script on\nINSERT INTO #Databases (Name)\nVALUES ('DB1'), ('DB2'), ('DB3'); -- replace with your database names\n\n-- Declare the script you want to run\nDECLARE @Script nvarchar(max) = 'SELECT * FROM [schema].[table]'; -- replace with your script\n\n-- Declare the database name variable\nDECLARE @DBName sysname;\n\n-- Create a cursor to iterate over the database names\nDECLARE cur CURSOR FOR SELECT Name FROM #Databases;\n\n-- Open the cursor\nOPEN cur;\n\n-- Fetch the first database name\nFETCH NEXT FROM cur INTO @DBName;\n\n-- Loop through each database\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Run the script on the current database\n    EXEC ('USE ' + @DBName + '; ' + @Script);\n\n    -- Fetch the next database name\n    FETCH NEXT FROM cur INTO @DBName;\nEND\n\n-- Close and deallocate the cursor\nCLOSE cur;\nDEALLOCATE cur;\n\n-- Drop the temporary table\nDROP TABLE #Databases;