CREATE PROCEDURE UpdateTables\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @Results TABLE (TableName sysname, RowCount int);\n\n    UPDATE Table1 Set Column = 0 WHERE Column IS NULL;\n    INSERT INTO @Results (TableName, RowCount) VALUES ('Table1', @@ROWCOUNT);\n\n    UPDATE Table2 Set Column = 0 WHERE Column IS NULL;\n    INSERT INTO @Results (TableName, RowCount) VALUES ('Table2', @@ROWCOUNT);\n\n    UPDATE Table3 Set Column = 0 WHERE Column IS NULL;\n    INSERT INTO @Results (TableName, RowCount) VALUES ('Table3', @@ROWCOUNT);\n\n    UPDATE Table4 Set Column = 0 WHERE Column IS NULL;\n    INSERT INTO @Results (TableName, RowCount) VALUES ('Table4', @@ROWCOUNT);\n\n    SELECT * FROM @Results;\nEND