CREATE TABLE IF NOT EXISTS blocks (\n    id INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    title VARCHAR(100),\n    defaultpage INT UNSIGNED NOT NULL,\n    PRIMARY KEY (id)\n) ENGINE=INNODB;\n\nCREATE TABLE IF NOT EXISTS pages (\n    pageID INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    title VARCHAR(50) NOT NULL,\n    content BLOB,\n    blockid INT UNSIGNED NOT NULL,\n    PRIMARY KEY (pageID)\n) ENGINE=INNODB;\n\nALTER TABLE blocks\nADD CONSTRAINT fk_blocks_pages FOREIGN KEY (defaultpage) REFERENCES pages (pageID);\n\nALTER TABLE pages\nADD CONSTRAINT fk_pages_blocks FOREIGN KEY (blockid) REFERENCES blocks (id);