CREATE TABLE synsets (\n  synset_id INT PRIMARY KEY,\n  pos CHAR(1),\n  gloss TEXT\n);\n\nCREATE TABLE words (\n  word_id INT PRIMARY KEY,\n  word VARCHAR(255),\n  synset_id INT,\n  FOREIGN KEY (synset_id) REFERENCES synsets(synset_id)\n);\n\nCREATE TABLE relations (\n  rel_id INT PRIMARY KEY,\n  synset1_id INT,\n  synset2_id INT,\n  rel_type VARCHAR(255),\n  FOREIGN KEY (synset1_id) REFERENCES synsets(synset_id),\n  FOREIGN KEY (synset2_id) REFERENCES synsets(synset_id)\n);