CREATE OR REPLACE TRIGGER sample_child_check\nAFTER INSERT OR UPDATE ON sample\nFOR EACH ROW\nDECLARE\n  child_count NUMBER;\nBEGIN\n  SELECT COUNT(*) INTO child_count\n  FROM (\n    SELECT 1 FROM concrete_table1 WHERE sample_id = :NEW.sample_id\n    UNION ALL\n    SELECT 1 FROM concrete_table2 WHERE sample_id = :NEW.sample_id\n    -- Add more concrete tables as needed\n  );\n  \n  IF child_count != 1 THEN\n    RAISE_APPLICATION_ERROR(-20001, 'Sample must have exactly one child entry');\n  END IF;\nEND;