-- Add a new GUID column to a table\nALTER TABLE Customers\nADD CustomerGuid uniqueidentifier DEFAULT (newid());\n\n-- Update a foreign key constraint to reference the new GUID column\nALTER TABLE Orders\nDROP CONSTRAINT FK_Orders_Customers;\n\nALTER TABLE Orders\nADD CONSTRAINT FK_Orders_Customers\nFOREIGN KEY (CustomerGuid) REFERENCES Customers(CustomerGuid);\n\n-- Migrate data from the old primary key column to the new GUID column\nUPDATE Customers\nSET CustomerGuid = NEWID();\n\n-- Update dependent tables to use the new GUID column\nUPDATE Orders\nSET CustomerGuid = c.CustomerGuid\nFROM Orders o\nINNER JOIN Customers c ON o.CustomerID = c.CustomerID;