CREATE OR REPLACE FUNCTION track_parameter_changes()\nRETURNS TRIGGER AS $$\nBEGIN\n    INSERT INTO parameter_changes (run_id, parameter_id, old_value, new_value)\n    VALUES (NEW.run_id, NEW.parameter_id, OLD.value, NEW.value);\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER track_parameter_changes_trigger\nAFTER UPDATE ON parameters\nFOR EACH ROW\nEXECUTE PROCEDURE track_parameter_changes();