DECLARE\n  l_cursor NUMBER;\n  l_desc_tab DBMS_SQL.DESC_TAB;\n  l_col_cnt NUMBER;\n  l_xml VARCHAR2(4000);\n\nBEGIN\n  l_cursor := DBMS_SQL.OPEN_CURSOR;\n  DBMS_SQL.PARSE(l_cursor, 'SELECT * FROM your_table', DBMS_SQL.NATIVE);\n  DBMS_SQL.DESCRIBE_COLUMNS(l_cursor, l_col_cnt, l_desc_tab);\n\n  FOR i IN 1..l_col_cnt LOOP\n    IF l_desc_tab(i).col_type = 1 THEN\n      -- NUMBER\n      l_xml := l_xml || '<column name="' || l_desc_tab(i).col_name || '" type="number"/>';\n    ELSIF l_desc_tab(i).col_type = 2 THEN\n      -- VARCHAR2\n      l_xml := l_xml || '<column name="' || l_desc_tab(i).col_name || '" type="string"/>';\n    -- Add more conditions for other data types as needed\n    END IF;\n  END LOOP;\n\n  DBMS_SQL.CLOSE_CURSOR(l_cursor);\n\n  -- Process the rows and emit the XML\n  FOR rec IN (SELECT * FROM your_table) LOOP\n    -- Use the column data types to emit the XML\n    FOR i IN 1..l_col_cnt LOOP\n      IF l_desc_tab(i).col_type = 1 THEN\n        l_xml := l_xml || '<' || l_desc_tab(i).col_name || '>' || TO_CHAR(rec.(i)) || '</' || l_desc_tab(i).col_name || '>';\n      ELSIF l_desc_tab(i).col_type = 2 THEN\n        l_xml := l_xml || '<' || l_desc_tab(i).col_name || '>' || rec.(i) || '</' || l_desc_tab(i).col_name || '>';\n      -- Add more conditions for other data types as needed\n      END IF;\n    END LOOP;\n  END LOOP;\nEND;