CREATE TABLE CrossReference (\n    CardId INT,\n    RulingId INT,\n    ReferenceType VARCHAR(10)  -- 'Direct' or 'Indirect'\n);\n\n-- Create a full-text index on the text column of the rulings table\nCREATE FULLTEXT INDEX ON rulings (text);\n\n-- Populate the CrossReference table\nINSERT INTO CrossReference (CardId, RulingId, ReferenceType)\nSELECT \n    c.Id, \n    r.Id, \n    'Direct'\nFROM \n    cards c\nINNER JOIN \n    rulings r ON c.Id = r.CardId\n\nUNION ALL\n\nSELECT \n    c.Id, \n    r.Id, \n    'Indirect'\nFROM \n    cards c\nINNER JOIN \n    rulings r ON CONTAINS(r.text, '"' + c.Name + '"');