-- Decrypt data using existing key and password\nDECLARE @DecryptedData VARBINARY(MAX);\nSET @DecryptedData = CONVERT(VARBINARY(MAX), DECRYPTBYKEY(ExistingSymmetricKey, 'ExistingPassword', EncryptedData));\n\n-- Re-encrypt data using new key and password\nDECLARE @EncryptedData VARBINARY(MAX);\nSET @EncryptedData = ENCRYPTBYKEY(NewSymmetricKey, 'NewStrongPassword', @DecryptedData);